mapper.select:
select goods_id from goods_coupon_config where id in 

	#{id}

group by goods_coupon_config.goods_id

SELECT
goods_coupon_config.id id, goods_coupon_config.good_value goodValue, goods_coupon_config.update_user_id update_user_id, goods_coupon_config.update_time update_time, goods_coupon_config.del_flag del_flag, nideshop_goods.id goodsId, nideshop_goods.category_id categoryId, nideshop_category.NAME categoryName, nideshop_goods.name name, nideshop_goods.create_user_dept_id create_user_dept_id, nideshop_attribute_category.NAME attributeCategoryName, nideshop_brand. NAME brandName, sys_dept.NAME deptName, sys_dept.dept_id deptId FROM goods_coupon_config LEFT JOIN nideshop_goods ON nideshop_goods.id = goods_coupon_config.goods_id LEFT JOIN nideshop_category ON nideshop_goods.category_id = nideshop_category.id LEFT JOIN nideshop_attribute_category ON nideshop_goods.attribute_category = nideshop_attribute_category.id LEFT JOIN nideshop_brand ON nideshop_brand.id = nideshop_goods.brand_id LEFT JOIN sys_user ON nideshop_goods.create_user_id = sys_user.user_id LEFT JOIN sys_dept ON sys_dept.dept_id = sys_user.dept_id WHERE 1=1
AND name LIKE concat('%',#{name},'%')
AND goods_id = ${goodsId}
AND goods_coupon_config.id = #{ id }
order by ${sidx} ${order}
order by id desc
limit #{offset}, #{limit}
